function SearchAlgoritm() {
    var d = document.getElementById("SearchingLine");
    d.value = d.value.replace(/[^\sа-яА-Яa-zA-Z1-9.-]+/g, '');
    var g = d.value.trim().replace(/[\s.-]+/g, '').toUpperCase();
    var b = document.getElementById("G").getElementsByTagName("li");
    if (g) { for (var e = 0; e < b.length; e++) { var c = b[e].getElementsByTagName("a")[0]; if (c.className == 'header') { b[e].style.display = "none"; continue; } else if (c.innerHTML.replace(/[^а-яА-Яa-zA-Z1-9]+/g, '').toUpperCase().indexOf(g) != -1) { b[e].style.display = ""; } else { b[e].style.display = "none"; } } } else for (var e = 0; e < b.length; e++) b[e].style.display = "";
    var s = document.getElementById("book-type").value
    if (s) filter(s,b);
}function filter(s,b) { for (var e = 0; e < b.length; e++) { var c = b[e].getElementsByTagName('a')[0].getAttribute('t'); if (c != s) b[e].style.display = "none"; else b[e].style.display = "";}};